// export default Notes}

const Notes = () => {
    const context = useContext(noteContext);
    const {notes, setNotes} = context;
  return (
    <div className="row my-3">
          <h2>Your Notes</h2>
          {notes.map((note)=>{
            return <NoteItem key={note.id} note={note}/>
          })}
      </div>
  )
}

export default Notes
