import { useState } from "react";
import NoteContext from "./noteContext";

const NoteState = (props) => {
  const host = "http://localhost:5000";
  const notesInitial = []
  const [notes, setNotes] = useState(notesInitial);

  // Fetch  user all notes on database
  const getNote =async (title, description, tag) => {
    // ToDo: API call
    const responce = await fetch(`${host}/api/notes/addnote`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjczZTAxOTJjYjRiZWJjOWVhODY1NmViIn0sImlhdCI6MTczMjE4MDc1MX0.-50uYZWVsd40QuqZNjNxs_3q9YoUovXNzCtrk9esKeg"
      },
      body: JSON.stringify({ title, description, tag }),
    });


    console.log("adding a new note");
    let note = {
      _id: "6744068dc5963aee5981e57b7",
      user: "673e0192cb4bebc9ea8656eb",
      title: title,
      description: description,
      tag: tag,
      date: "2024-11-25T05:09:33.564Z",
      __v: 0,
    };
    setNotes(notes.concat(note));
  };

  // Add a new note
  const addNote =async (title, description, tag) => {
    // ToDo: API call
    const responce = await fetch(`${host}/api/notes/addnote`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjczZTAxOTJjYjRiZWJjOWVhODY1NmViIn0sImlhdCI6MTczMjE4MDc1MX0.-50uYZWVsd40QuqZNjNxs_3q9YoUovXNzCtrk9esKeg"
      },
      body: JSON.stringify({ title, description, tag }),
    });


    console.log("adding a new note");
    let note = {
      _id: "6744068dc5963aee5981e57b7",
      user: "673e0192cb4bebc9ea8656eb",
      title: title,
      description: description,
      tag: tag,
      date: "2024-11-25T05:09:33.564Z",
      __v: 0,
    };
    setNotes(notes.concat(note));
  };

  // Delete a existing note
  const deleteNote = (id) => {
    // ToDo :  API call
    console.log("Deleting note with id" + id);
    const newNotes = notes.filter((note) => {
      return note._id !== id;
    });
    setNotes(newNotes);
  };
  // Edit a existing note
  
  const editNote = async (id, title, description, tag) => {
    // ToDo: API call
    const responce = await fetch(`${host}/api/notes/updatenote/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjczZTAxOTJjYjRiZWJjOWVhODY1NmViIn0sImlhdCI6MTczMjE4MDc1MX0.-50uYZWVsd40QuqZNjNxs_3q9YoUovXNzCtrk9esKeg"
      },
      body: JSON.stringify({ title, description, tag }),
    });
    
    const json = responce.json();

    //logic to edit notes in client

    for (let index = 0; index < notes.length; index++) {
      const element = notes[index];
      if (element._id === id) {
        element.title = title;
        element.description = description;
        element.tag = tag;
      }
    }
  };
    
  return (
    <NoteContext.Provider value={{ notes, addNote, deleteNote, editNote }}>
       {props.children}
    </NoteContext.Provider>
  );
};

export default NoteState;
