import React, { useContext, useState } from 'react';
import noteContext from '../context/notes/noteContext';

const AddNote = () => {
    const context = useContext(noteContext);
    const {addNote} = context;


    const [note, setnote] = useState({title : "", description : "", tag : ""});
    const handleClick =(e)=>{
      e.preventDefault();
      addNote(note);
    }
    const onChange = (e)=>{
      setnote({...note, [e.target.name]: e.target.value})
    }
  return (
    <div className="container my-3">
          <h2>Add a New Note</h2>
          <form className="my-3">
            <div className="mb-3">
              <label htmlFor="title" className="form-label"><b>Title</b> </label>
              <input type="text" className="form-control" id="title" name="title" aria-describedby="emailHelp" onChange={onChange}/>
              <div id="emailHelp" className="form-text">Only You can see your <b>inotebooks</b> Notes</div>
            </div>
            <div className="mb-3">
              <label htmlFor="description" className="form-label"><b>Description</b></label>
              <input type="text" className="form-control" id="description" name="description" onChange={onChange}/>
            </div>
            <div className="mb-3 form-check">
              <input type="checkbox" className="form-check-input" id="exampleCheck1"/>
              <label className="form-check-label" htmlFor="exampleCheck1">Check me In</label>
            </div>
            <button type="submit" className="btn btn-primary" onClick = {handleClick} >Submit</button>
          </form> 
      </div>
  )
}

export default AddNote
