import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Signup = () => {
  const host = "http://localhost:5000";

  let navigate = useNavigate();
  const [credentials, setcredentials] = useState({email: "", password: ""});

  const handleSubmit= async (e)=>{
    e.preventDefault();
    const responce = await fetch(`${host}/api/auth/createuser`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({email: credentials.email, password: credentials.password}),
      });
      const json = await responce.json();
      console.log(json);
      // console.log("Form submitted");
      if(json.success){
        // redirect if user is logged in to users notes page and save the auth token
        localStorage.setItem('token', json.authtoken);
        navigate("/");
      }
      else{
        alert("Invalid Credentials");
      }
}

const onChange = (e)=>{
  setcredentials({...credentials, [e.target.name]: e.target.value})
  }

  return (
    <div className="container my-5">
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="name" className="form-label"><b>Enter Your Name</b></label>
          <input type="text" className="form-control" id="name" onChange={onChange}  aria-describedby="emailHelp"/>
        </div>
        <div className="mb-3">
          <label htmlFor="email" className="form-label"><b>Email address</b></label>
          <input type="email" className="form-control" id="email" onChange={onChange}  aria-describedby="emailHelp"/>
          <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label"><b>Password</b></label>
          <input type="password" className="form-control" id="password" onChange={onChange} />
        </div>
        <div className="mb-3">
          <label htmlFor="cpassword" className="form-label"><b>Confirm Password</b></label>
          <input type="password" className="form-control" id="cpassword" onChange={onChange} />
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  )
}

export default Signup
