import { useState } from "react";
import NoteContext from "./noteContext";

const NoteState = (props) =>{
  const host = "http://localhost:5000";

  const notesInitial = [
        {
          "_id": "673f45e069a56cff22281360d",
          "user": "673e0192cb4bebc9ea8656eb",
          "title": "my Title",
          "description": "dummy description",
          "tag": "personal",
          "date": "2024-11-21T14:38:24.396Z",
          "__v": 0
        },
        {
          "_id": "673f45f769a56cff2223283611",
          "user": "673e0192cb4bebc9ea8656eb",
          "title": "my Title",
          "description": "dummy description",
          "tag": "personal",
          "date": "2024-11-21T14:38:47.104Z",
          "__v": 0
        },
        {
          "_id": "67440688c593aee5981e57b1",
          "user": "673e0192cb4bebc9ea8656eb",
          "title": "Nwe Playlist",
          "description": "dummy youtube description",
          "tag": "youtube",
          "date": "2024-11-25T05:09:28.588Z",
          "__v": 0
        },
        {
          "_id": "6744068ac5943aee5981e57b3",
          "user": "673e0192cb4bebc9ea8656eb",
          "title": "Nwe Playlist",
          "description": "dummy youtube description",
          "tag": "youtube",
          "date": "2024-11-25T05:09:30.940Z",
          "__v": 0
        },
        {
          "_id": "6744068dc593a5ee5981e57b5",
          "user": "673e0192cb4bebc9ea8656eb",
          "title": "Nwe Playlist",
          "description": "dummy youtube description",
          "tag": "youtube",
          "date": "2024-11-25T05:09:33.196Z",
          "__v": 0
        }
      ] 


  const [notes, setNotes] = useState(notesInitial);

  // Add a new note
  const addNote = (title, description, tag) => {
    // ToDo: API call 
    console.log("adding a new note");
    let note =  {
      "_id": "6744068dc5963aee5981e57b7",
      "user": "673e0192cb4bebc9ea8656eb",
      "title": title,
      "description": description,
      "tag": tag,
      "date": "2024-11-25T05:09:33.564Z",
      "__v": 0
    };
    setNotes(notes.concat(note))
  }
  // Delete a existing note
  const deleteNote = (id) => {
     // ToDo :  API call 
    console.log("Deleting note with id" + id);
    const newNotes = notes.filter ((note)=> {return note._id !== id });
    setNotes(newNotes)
  }
  // Edit a existing note
  const editNote = async (id, title, description, tag) => {
      // ToDo: API call
      const url = `${host}/api/notes/updatenote/673f472cd1559f479278696b`;
        try {
          const response = await fetch(url);
          if (!response.ok) {
            throw new Error(`Response status: ${response.status}`);
          }

          const json = await response.json();
          console.log(json);

        } catch (error) {
          console.error(error.message);
        }
      //logic to edit notes in client
      for (let index = 0; index < notes.length; index++) {
        const element = notes[index];
        if (element._id === id) {
        element.title = title;
        element.description = description;
        element.tag = tag;
      }
    }
  }


  return(
        <NoteContext.Provider value = {{notes, addNote, deleteNote, editNote}}>
           { props.children }
        </NoteContext.Provider>
    )
}

export default NoteState;
